import React, {useState} from "react";

const CadastrarPerguntas = () => {

    const [anuncio, serAnuncio] = useState({
        title: "",
        category: "",
        content: ""
    })

    const valueInput = e => serAnuncio({...anuncio, [e.target.title]: e.target.value})

    const registerAnuncio = async (e) => {
        e.preventDefault();

        if(e.target.title.value != ''){
            console.log('diferente')

            setAnuncio({
                title: "",
                category: "",
                content: ""
            })
        }else{
            console.log("igual")
        }
    }   

    return(
        <div className="Cadastro">
            <form onSubmit={registerAnuncio}>
                <label htmlFor="title" className="sr-only">Titulo do anuncio</label>
                <input name="title"  type="text" placeholder="Titulo:" onChange={valueInput} value={anuncio.title}></input>

                <label htmlFor="category" className="sr-only">Categoria do anuncio</label>
                <input name="category" type="text" placeholder="Categoria:" onChange={valueInput} value={anuncio.category}></input>

                <label htmlFor="content" className="sr-only">Conteudo do anuncio</label>
                <textarea name="content" type="text" placeholder="Conteudo:" onChange={valueInput} value={anuncio.content} ></textarea>

                <button type="Submit">
                    Cadastrar
                </button>
            </form>
        </div>
    )
}

export default CadastrarPerguntas;
