    }

    return(
        <div className="Cadastro">
            <form onSubmit={handleSubmit(registerAnuncio)}>
                <label htmlFor="title" className="sr-only">Titulo do anuncio</label>
                <input {...register("title")}  type="text" placeholder="Titulo:"></input>

                <label htmlFor="category" className="sr-only">Categoria do anuncio</label>
                <input {...register("category")} type="text" placeholder="Categoria:"></input>

                <label htmlFor="content" className="sr-only">Conteudo do anuncio</label>
                <textarea {...register("content")} type="text" placeholder="Conteudo:"></textarea>

                <button type="Submit">
                    Cadastrar
                </button>
            </form>
        </div>
    )
}

export default CadastrarPerguntas;
