import React, {useEffect, useState} from "react";
import axios from 'axios';
import Header from '../../components/Header';
import Main from '../../components/Main';

const Home = () =>{
    const [listGames, setListGames] = useState();

    const [listSearch, setListSearch] = useState();


    console.log(listSearch)
    useEffect(() => {
        axios.get("http://localhost:3001/").then((response) => { 
            setListGames(response.data); 
        });
    }, []);

    useEffect(() => {
        axios.get(`http://localhost:3001/pesquisa`).then((response) => {
            setListSearch(response.data);
        });
    }, [])

    return(
        <>
            <header className="App-header">
                <Header></Header>
            </header>

            <main>
                {typeof listGames !== "undefined" && listGames.map((value) => {
                        return (
                            <Main 
                                key={value.id}
                                listGames={listGames}
                                setListGames={setListGames}
                                id={value.id}
                                title={value.titulo}
                                category={value.categoria}
                                content={value.assunto}
                            />
                        )
                })}

                {typeof listSearch !=="undefined" && listSearch.map((val) => {
                    return (
                        <Main 
                            key={val.id}
                            listSearch={listSearch}
                            setListSearch={setListSearch}
                            id={val.id}
                            title={val.titulo}
                            category={val.categoria}
                            content={val.assunto}
                        />
                    )
                })}
            </main>

            <footer>
                <h2>DESENVOLVIDO POR JOAO MANOEL</h2>
            </footer>
        </>
    );
}

export default Home;
